import { IMessageHandlers } from "@screencloud/app-core";
import { AppMessages, AppPlayerMessages } from "../index";
/**
 * Plain object providing typed message handlers for all container-to-app messages.
 *
 * @see AppMessageTypes
 */
export interface IAppPlayerMessageHandlers extends Partial<IMessageHandlers> {
    [index: string]: undefined | ((payload: void | any) => void);
    initialize?: AppPlayerMessages.Initialize.Handler;
    start?: AppPlayerMessages.Start.Handler;
    stateUpdated?: AppPlayerMessages.StateUpdated.Handler;
    finish?: AppPlayerMessages.Finish.Handler;
}
export interface IAppPlayerHostMessageHandlers {
    [index: string]: undefined | ((payload: void | any) => (void | Promise<any>));
    finished?: AppMessages.Finished.Handler;
    finishing?: AppMessages.Finishing.Handler;
    initialized?: AppMessages.Initialized.Handler;
    preloaded?: AppMessages.Preloaded.Handler;
    requestAuthToken?: AppMessages.RequestAuthToken.Handler;
    showing?: AppMessages.Showing.Handler;
    started?: AppMessages.Started.Handler;
    stateUpdate?: AppMessages.StateUpdate.Handler;
    log?: AppMessages.Log.Handler;
}
